import React from 'react';
import Brick from './Brick';
import './GameBlock.css';

class GameBlock extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            grid: this.newItem((() => {
                var ret = Array(16);
                for (let i = 0;i < ret.length; i++) ret[i] = 0;
                return ret;
            })()), 
            flag: 0
        }
        this.newItem = this.newItem.bind(this);
        this.handleKeyPress = this.handleKeyPress.bind(this);
    }
    componentDidMount() {
        document.addEventListener("keydown", this.handleKeyPress);
    }
    componentWillUnmount() {
        document.removeEventListener("keydown", this.handleKeyPress);
    }
    checkIfLose () {
        return !(
            this.handleKeyLeft(this.state.grid) != this.state.grid || 
            this.handleKeyRight(this.state.grid) != this.state.grid || 
            this.handleKeyUp(this.state.grid) != this.state.grid || 
            this.handleKeyDown(this.state.grid) != this.state.grid
        );
    }
    newItem (theGrid) {
        var t_grid = Array.from(theGrid);
        var pos = [];
        for (let i = 0;i < t_grid.length; i++) {
            if (t_grid[i] === 0) pos.push(i);
        }
        var idx = Math.floor(Math.random()*(pos.length));
        if (Math.random() < 0.1) {
            t_grid[pos[idx]] = 2;
        } else {
            t_grid[pos[idx]] = 1;
        }
        return t_grid;
    }
    handleKeyLeft() {
        var t_grid = Array.from(this.state.grid);
        var merged = [];
        for (let i = 0;i < 4; i++) {
            for (let j = 0;j < 4; j++) {
                var k = j-1;
                while (k >= 0 && t_grid[i*4+k] === 0) k--;
                if (k >= 0 && t_grid[i*4+k] === t_grid[i*4+j] && !merged[i*4+k]) {
                    t_grid[i*4+j] = 0;
                    t_grid[i*4+k]++;
                    merged[i*4+k] = 1;

                } else {
                    t_grid[i*4+k+1] = t_grid[i*4+j];
                    if (k+1 !== j) t_grid[i*4+j] = 0;
                }
            }
        }
        return t_grid;
    }
    handleKeyRight() {
        var t_grid = Array.from(this.state.grid);
        var merged = [];
        for (let i = 0;i < 4; i++) {
            for (let j = 3;j >= 0; j--) {
                var k = j+1;
                while (k < 4 && t_grid[i*4+k] === 0) k++;
                if (k < 4 && t_grid[i*4+k] === t_grid[i*4+j] && !merged[i*4+k]) {
                    t_grid[i*4+j] = 0;
                    t_grid[i*4+k]++;
                    merged[i*4+k] = 1;
                } else {
                    t_grid[i*4+k-1] = t_grid[i*4+j];
                    if (k-1 !== j) t_grid[i*4+j] = 0;
                }
            }
        }
        return t_grid;

    }
    handleKeyUp() {
        var t_grid = Array.from(this.state.grid);
        var merged = [];
        for (let i = 0;i < 4; i++) {
            for (let j = 0;j < 4; j++) {
                var k = i-1;
                while (k >= 0 && t_grid[k*4+j] === 0) k--;
                if (k >= 0 && t_grid[k*4+j] === t_grid[i*4+j] && !merged[k*4+j]) {
                    t_grid[i*4+j] = 0;
                    t_grid[k*4+j]++;
                    merged[k*4+j] = 1;
                } else {
                    t_grid[(k+1)*4+j] = t_grid[i*4+j];
                    if (k+1 !== i) t_grid[i*4+j] = 0;
                }
            }
        }
        return t_grid;
    }
    handleKeyDown() {
        var t_grid = Array.from(this.state.grid);
        var merged = [];
        for (let i = 3;i >= 0; i--) {
            for (let j = 0;j < 4; j++) {
                var k = i+1;
                while (k < 4 && t_grid[k*4+j] === 0) k++;
                if (k < 4 && t_grid[k*4+j] === t_grid[i*4+j] && !merged[k*4+j]) {
                    t_grid[i*4+j] = 0;
                    t_grid[k*4+j]++;
                    merged[k*4+j] = 1;
                } else {
                    t_grid[(k-1)*4+j] = t_grid[i*4+j];
                    if (k-1 !== i) t_grid[i*4+j] = 0;
                }
            }
        }
        return t_grid;
    }
    handleKeyPress(event) {
        var updated_grid = (() => {
            if (event.keyCode === 37) {
                return this.handleKeyLeft();
            } else if (event.keyCode === 38) {
                return this.handleKeyUp();
            } else if (event.keyCode === 39) {
                return this.handleKeyRight();
            } else if (event.keyCode === 40) {
                return this.handleKeyDown();
            }
            return [];
        })();
        const flag = (() => {
            for (let i = 0;i < 16; i++) {
                if (updated_grid[i] != this.state.grid[i]) return true;
            }
            return false;
        })();
        if (flag) {
            updated_grid = this.newItem(updated_grid);
            this.setState({
                grid: updated_grid
            });
        }
    }
    render() {
        return (
            <div className='GameBlock'>
                <Brick value={this.state.grid[0]}/>
                <Brick value={this.state.grid[1]}/>
                <Brick value={this.state.grid[2]}/>
                <Brick value={this.state.grid[3]}/>
                <Brick value={this.state.grid[4]}/>
                <Brick value={this.state.grid[5]}/>
                <Brick value={this.state.grid[6]}/>
                <Brick value={this.state.grid[7]}/>
                <Brick value={this.state.grid[8]}/>
                <Brick value={this.state.grid[9]}/>
                <Brick value={this.state.grid[10]}/>
                <Brick value={this.state.grid[11]}/>
                <Brick value={this.state.grid[12]}/>
                <Brick value={this.state.grid[13]}/>
                <Brick value={this.state.grid[14]}/>
                <Brick value={this.state.grid[15]}/>
                <p>{this.checkIfLose()}</p>
            </div>
        );
    }
}

export default GameBlock;
