import React from 'react';
import Brick from './Brick';
import './GameBlock.css';

class GameBlock extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            grid: (() => {
                var ret = Array(16);
                for (let i = 0;i < ret.length; i++) ret[i] = 0;
                return ret;
            })(), 
            flag: 0
        }
        this.newItem = this.newItem.bind(this);
        this.handleKeyPress = this.handleKeyPress.bind(this);
        this.handleKeyUp = this.handleKeyUp.bind(this);
    }
    componentDidMount() {
        document.addEventListener("keydown", this.handleKeyPress);
    }
    componentWillUnmount() {
        document.removeEventListener("keydown", this.handleKeyPress);
    }
    newItem () {
        var t_grid = this.state.grid;
        var pos = [];
        for (let i = 0;i < t_grid.length; i++) {
            if (t_grid[i] === 0) pos.push(i);
        }
        var idx = Math.floor(Math.random()*(pos.length));
        if (Math.random() < 0.15) {
            t_grid[pos[idx]] = 2;
        } else {
            t_grid[pos[idx]] = 1;
        }
        this.setState({
            grid: t_grid
        });
    }
    handleKeyLeft() {

    }
    handleKeyRight() {

    }
    handleKeyUp() {
        var t_grid = this.state.grid;
        for (let i = 0;i < 4; i++) {
            for (let j = 0;j < 4; j++) {
                if (i == 0) continue;
                if (t_grid[i*4+j] > 0) {
                    if (t_grid[(i-1)*4+j] == 0) {
                        [t_grid[(i-1)*4+j], t_grid[i*4+j]] = [t_grid[i*4+j], t_grid[(i-1)*4+j]];
                    } else if (t_grid[(i-1)*4+j] == t_grid[i*4+j]) {
                        t_grid[i*4+j] = 0;
                        t_grid[(i-1)*4+j]++;
                    }
                }
            }
        }
    }
    handleKeyDown() {

    }
    handleKeyPress(event) {
        this.handleKeyUp();
        this.newItem();
    }
    render() {
        return (
            <div className='GameBlock'>
                <Brick value={this.state.grid[0]}/>
                <Brick value={this.state.grid[1]}/>
                <Brick value={this.state.grid[2]}/>
                <Brick value={this.state.grid[3]}/>
                <Brick value={this.state.grid[4]}/>
                <Brick value={this.state.grid[5]}/>
                <Brick value={this.state.grid[6]}/>
                <Brick value={this.state.grid[7]}/>
                <Brick value={this.state.grid[8]}/>
                <Brick value={this.state.grid[9]}/>
                <Brick value={this.state.grid[10]}/>
                <Brick value={this.state.grid[11]}/>
                <Brick value={this.state.grid[12]}/>
                <Brick value={this.state.grid[13]}/>
                <Brick value={this.state.grid[14]}/>
                <Brick value={this.state.grid[15]}/>
            </div>
        );
    }
}

export default GameBlock;
