import React from 'react';

const colorPalette = [
    '',
    'red',
    'blue',
    'green',
    'yellow',
    'black',
    'gray',
];

class Brick extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            brickId: this.props.brickId,
            styles: {
                position: 'absolute',
                margin: 'auto',
                borderRadius: '10%',
                width: '6vw',
                height: '6vw',
                top: this.initialPosition(this.props.brickId)[0],
                left: this.initialPosition(this.props.brickId)[1],
                background: "gray",
                visibility: 'hidden',
                fontSize: 40,
                justifyContent: 'center',
                alignItems: 'center',
                textAlign: 'center',
                fontFamily: 'TimesNewRoman'
            }
        };
        this.initialPosition = this.initialPosition.bind(this);
        this.romanNumeral = this.romanNumeral.bind(this);
        this.updateStyles = this.updateStyles.bind(this);
    }
    componentDidUpdate (prevProps, prevState, snapshot) {
        if (prevProps.value != this.props.value) {
            this.updateStyles();
        }
    }
    updateStyles() {
        var t_styles = JSON.parse(JSON.stringify(this.state.styles));
        t_styles.background = colorPalette[this.props.value];
        if (this.props.value === 0) t_styles.visibility= 'hidden';
        else t_styles.visibility= 'visible';
        this.setState({
            styles: t_styles
        });
    }
    initialPosition (brickId) {
        const i = Math.floor(brickId/4), j = brickId%4;
        return [`${i*7.5+0.75}vw`, `${j*7.5+0.75}vw`];
    }
    romanNumeral () {
        const num = this.props.value;
        if (num === 0) return '';
        var digits = String(+num).split(""),
            key = ["","C","CC","CCC","CD","D","DC","DCC","DCCC","CM",
               "","X","XX","XXX","XL","L","LX","LXX","LXXX","XC",
               "","I","II","III","IV","V","VI","VII","VIII","IX"],
            roman = "",
            i = 3;
        while (i--)
            roman = (key[+digits.pop() + (i * 10)] || "") + roman;
        return Array(+digits.join("") + 1).join("M") + roman;
    }
    render() {
        this.updateStyles();
        return (
            <div style={this.state.styles}>
                <p>{this.romanNumeral()}</p>
            </div>
        );
    }
}

export default Brick;
